import {
  Drawer,
  IconButton,
  List,
  ListItem,
  ListItemButton,
  ListItemIcon,
  ListItemText,
} from "@mui/material";
import { useState } from "react";
import MenuIcon from "@mui/icons-material/Menu";
import { Link } from "react-router-dom";

export default function DrawerComp() {
  const [open, setOpen] = useState(false);
  return (
    <>
      <Drawer
        PaperProps={{
          sx: { backgroundColor: "#f5d5dd" },
        }}
        open={open}
        onClose={() => setOpen(false)}
      >
        <List>
          <ListItemButton>
            <ListItem>
              <ListItemText button component={Link} to={"/"}>
                Home
              </ListItemText>
            </ListItem>
          </ListItemButton>
          <ListItemButton>
            <ListItemText button component={Link} to={"/contacts"}>
              Contacts
            </ListItemText>
          </ListItemButton>
          <ListItemButton>
            <ListItemText to={"/register"} component={Link}>
              Register
            </ListItemText>
          </ListItemButton>
          <ListItemButton>
            <ListItemText>LogIn</ListItemText>
          </ListItemButton>
        </List>
      </Drawer>
      <IconButton
        sx={{ marginLeft: "auto", color: "white" }}
        onClick={() => setOpen(!open)}
      >
        <MenuIcon></MenuIcon>
      </IconButton>
    </>
  );
}
