import { useDispatch, useSelector } from "react-redux";
import authSelectors from "../redux/auth/auth-selector";
import authOperation from "../redux/auth/auth-operations";
import s from "./AppBar.module.css";
import { Button, Typography, Grid } from "@mui/material";

export default function UserMenu({ isLarge }) {
  const dispatch = useDispatch();
  const name = useSelector(authSelectors.getUserName);
  return (
    <>
      {isLarge === false ? (
        <Grid item xs={3} sx={{ marginRight: 12 }}>
          <Button
            type="submit"
            variant="contained"
            onClick={() => dispatch(authOperation.logOut())}
            sx={{
              marginLeft: "auto",
              backgroundColor: "white",
              color: "black",
              "&:hover": {
                backgroundColor: "#e3e2e2",
                color: "black",
              },
            }}
          >
            LogOut
          </Button>
        </Grid>
      ) : (
        <Grid>
          <Typography color="black">Hello, {name}</Typography>
          <Grid item xs={3} sx={{ marginRight: 12 }}>
            <Button
              type="submit"
              variant="contained"
              onClick={() => dispatch(authOperation.logOut())}
              sx={{
                marginLeft: "auto",
                backgroundColor: "white",
                color: "black",
                "&:hover": {
                  backgroundColor: "#e3e2e2",
                  color: "black",
                },
              }}
            >
              LogOut
            </Button>
          </Grid>
        </Grid>
      )}
    </>
  );
}
